function Banner(props) {
	let styleImage = {
		backgroundRepeat: "no-repeat",
		}
  return (
		<div loading="lazy" className="banner" style={{...styleImage, ...props.bannerStyle}} >
		<div className={"banner--content " + props.className}  style={{display:"flex"}}>
		{props.children}
			</div>
</div>
  );
}

export default Banner;


