import Card from "../Card";
import "./Category.css";
import "bootstrap/dist/css/bootstrap.css";
// import Carousel from "react-bootstrap/Carousel";
// import Slider from '../Slider'
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import { Navigation } from "swiper/modules";

import React, { useLayoutEffect, useState } from "react";

function Version(props) {
  const [size, setSize] = useState([0, 0]);
  useLayoutEffect(() => {
    function updateSize() {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener("resize", updateSize);
    updateSize();
    return () => window.removeEventListener("resize", updateSize);
  }, []);

//  if (size[0] < 620) {
//    return (
//      <Swiper
//        slidesPerView={1}
//				spaceBetween={10}
//        loop={true}
//        navigation={true}
//        modules={[Navigation]}
//				style={{height:"100%"
//}}
//      >
//        {props.cards.map((e) => (
//          <SwiperSlide
//            style={{
//              display: "flex",
//              justifyContent: "center",
//              alignItems: "center",
//            }}
//						className="card--slide"
//							key={e.description + e.modelo + "--slider"}
//          >
//            <Card
//							propsStyle={{...props.cardStyle}}
//              frameColor={props.frameColor}
//              description={e.description}
//              modelo={e.modelo}
//              preco={e.preco}
//              img={e.img}
//	      discount ={e.discount}
//              btnStyle={props.btnStyle}
//              btnMensagemPrefixo={props.btnMensagemPrefixo}
//							id={e.id}
//							key={e.description + e.modelo}
//							iconeSize={props.iconeSize}
//							delay={props.delay}
//            />
//          </SwiperSlide>
//        ))}
//      </Swiper>
//    );
//  }
  return (
    <div style={{ display: "flex", marginBottom: "64px"}}>
      <div className="category--grid"
      >
        {props.cards.map((e) => (
          <Card
            propsStyle={{ ...props.cardStyle }}
            logo={e.logo}
            frameColor={props.frameColor}
            description={e.description}
            modelo={e.modelo}
            preco={e.preco}
	    preco_antigo={e.preco_antigo}
	    id={e.id}
	    img={e.img}
            discount={e.discount}
            btnStyle={props.btnStyle}
            btnMensagemPrefixo={props.btnMensagemPrefixo}
						key={e.description + e.modelo}
						iconeSize={props.iconeSize}
						delay={props.delay}
          />
        ))}
      </div>
    </div>
  );
}

function Category(props) {
	if (!props.cards) return <></>
  return (
    <div
      id={props.titulo.replaceAll(" ", "_").replaceAll("//","--").replaceAll("Ó","O",)}
      className="category"
      style={{ backgroundColor: props.bg}}
    >
      <h1 className="category--title" >{props.titulo.toUpperCase().split("//")[1]}</h1>
			<h2 className="category--subtitle" >{props.titulo.toLowerCase().split("//")[0]}</h2>
      <div className="category--grid"
      >
        {props.cards.map((e) => (
          <Card
            propsStyle={{ ...props.cardStyle }}
            logo={e.logo}
            frameColor={props.frameColor}
            description={e.description}
            modelo={e.modelo}
            preco={e.preco}
	    preco_antigo={e.preco_antigo}
	    id={e.id}
	    img={e.img}
            discount={e.discount}
            btnStyle={props.btnStyle}
            btnMensagemPrefixo={props.btnMensagemPrefixo}
						key={e.description + e.modelo}
						iconeSize={props.iconeSize}
						delay={props.delay}
          />
        ))}
		{props.cards.length % 2 ? <div className="card__custom"></div> : ""}
      </div>
      {props.banner}
    </div>
  );
}

export default Category;
